                    });
                });
            });

            retakeBtn.addEventListener(''click'', function() {
                answeredCount = 0;
                correctCount = 0;

                questions.forEach(question => {
                    question.classList.remove(''answered'');
                    const options = question.querySelectorAll(''.quiz-option'');
